'use strict';

/**
 * @param {Boolean} value - for property 'visible'
 * @return {Boolean} - update current property value
 */
export function update(value) {

	return value;
}

/**
 * @param {CursorEvent} event
 */
export function cursorClick(event) {
	// Turn off sound
	if (thisScene.getLayer("Toggle_Sound_Button_OFF").visible == false && thisScene.getLayer("Toggle_Sound_Button_ON").visible == true)
	{
		thisScene.getLayer("Toggle_Sound_Button_OFF").visible = true;
		thisScene.getLayer("Toggle_Sound_Button_ON").visible = false;

		// check to see if a movie wallpaper is enabled

		// Verum Rex Title Screen
		if (thisScene.getLayer("Verum_Rex_Title_Screen").visible == true)
		{
				thisScene.getLayer("Verum_Rex_Title_OST_Track").stop();
		}
		// Verum Rex Trailer
		else if (thisScene.getLayer("Verum_Rex_Trailer").visible == true)
		{
				thisScene.getLayer("Verum_Rex_Trailer_OST_Track").stop();
		}
		// Kingdom Hearts 3 Secret Movie - Yozora
		else if (thisScene.getLayer("KH3_Yozora").visible == true)
		{
				thisScene.getLayer("Secrets_of_The_Night_OST_Track").stop();
		}
	}
	// Turn on sound
	else if (thisScene.getLayer("Toggle_Sound_Button_ON").visible == false && thisScene.getLayer("Toggle_Sound_Button_OFF").visible == true)
	{
		thisScene.getLayer("Toggle_Sound_Button_ON").visible = true;
		thisScene.getLayer("Toggle_Sound_Button_OFF").visible = false;

		// check to see if the title screen wallpaper is enabled

		// Verum Rex Title Scree
		if (thisScene.getLayer("Verum_Rex_Title_Screen").visible == true)
		{
				thisScene.getLayer("Verum_Rex_Title_OST_Track").play();
		}
		// Verum Rex Trailer
		else if (thisScene.getLayer("Verum_Rex_Trailer").visible == true)
		{
				thisScene.getLayer("Verum_Rex_Trailer_OST_Track").play();
		}
		// Kingdom Hearts 3 Secret Movie - Yozora
		else if (thisScene.getLayer("KH3_Yozora").visible == true)
		{
				thisScene.getLayer("Secrets_of_The_Night_OST_Track").play();
		}
	}
}

'use strict';

var isMenuOpened = false;
/**
 * @param {Boolean} value - for property 'visible'
 * @return {Boolean} - update current property value
 */
export function update(value) {

	return value;
}


/**
 * @param {CursorEvent} event
 */
export function cursorClick(event) {


	// Open or close menu
	if (!isMenuOpened)
	{
		const oneButton = thisScene.getLayer("1_Button");
		oneButton.origin =  new Vec3(7474.80176,3644.96216,0);

		const twoButton = thisScene.getLayer("2_Button");
		twoButton.origin =  new Vec3(7474.80176,3235.11279,0);

		const threeButton = thisScene.getLayer("3_Button");
		threeButton.origin =  new Vec3(7474.80176,2825.26343,0);

		isMenuOpened = true;
	}
	else if (isMenuOpened)
	{
		const oneButton = thisScene.getLayer("1_Button");
		oneButton.origin =  new Vec3(3322.19336,5309.52637,0);

		const twoButton = thisScene.getLayer("2_Button");
		twoButton.origin =  new Vec3(3630.41992,5309.52637,0);

		const threeButton = thisScene.getLayer("3_Button");
		threeButton.origin =  new Vec3(3934.15771,5309.52637,0);

		isMenuOpened = false;
	}

	// Check if the main static wallpaper visibility is false. If so, stop any of the movies and restore the main static wallpaper
	if (thisScene.getLayer("Verum_Rex_Main").visible == false)
	{
		thisScene.getLayer("Verum_Rex_Main").visible = true;

		// Verum Rex Title Screen
		thisScene.getLayer("Verum_Rex_Title_Screen").visible = false;
		const titleScreenLayer = thisScene.getLayer("Verum_Rex_Title_Screen").getVideoTexture(0);
		titleScreenLayer.stop();
		thisScene.getLayer("Verum_Rex_Title_OST_Track").stop();

		// Verum Rex Trailer
		thisScene.getLayer("Verum_Rex_Trailer").visible = false;
		const trailerLayer = thisScene.getLayer("Verum_Rex_Trailer").getVideoTexture(0);
		trailerLayer.stop();
		thisScene.getLayer("Verum_Rex_Trailer_OST_Track").stop();

		// Kingdom Hearts 3 Secret Movie - Yozora
		thisScene.getLayer("KH3_Yozora").visible = false;
		const yozoraLayer = thisScene.getLayer("KH3_Yozora").getVideoTexture(0);
		yozoraLayer.stop();
		thisScene.getLayer("Secrets_of_The_Night_OST_Track").stop();

		isMenuOpened = false; // reset menu failsafe
	}
}


